const ID_MAP: Record<string, string> = {
  'UZAN': 'andijon',
  'UZBU': 'buxoro',
  'UZFA': 'fargona',
  'UZJI': 'jizzax',
  'UZNG': 'namangan',
  'UZNW': 'navoiy',
  'UZQA': 'qarshi',
  'UZQR': 'nukus',
  'UZSA': 'samarqand',
  'UZSI': 'guliston',
  'UZSU': 'termiz',
  'UZTK': 'toshkent',
  'UZTO': 'toshkent',
  'UZXO': 'urganch'
};

interface UzbekistanMapProps {
  onRegionSelect?: (regionId: string) => void;
  className?: string;
  selectedRegion?: string | null;
}

export function UzbekistanMap({ onRegionSelect, className, selectedRegion }: UzbekistanMapProps) {
  const [hoveredRegion, setHoveredRegion] = useState<string | null>(null);

  const handleRegionClick = (regionId: string) => {
    // Map the SVG ID to the API ID if needed, or just pass the SVG ID
    const apiId = ID_MAP[regionId] || regionId.toLowerCase();
    if (onRegionSelect) {
      onRegionSelect(apiId);
    }
  };

  return (
    <div className={cn("w-full h-full flex flex-col items-center justify-center relative", className)}>
      <div className="relative w-full aspect-[1000/652]">
        <svg
          viewBox="0 0 1000 652"
          className="w-full h-full drop-shadow-xl"
          style={{ filter: 'drop-shadow(0 0 10px rgba(0,0,0,0.1))' }}
        >
          <g id="features">
            {regions.map((region) => {
              // Map API ID back to SVG ID for selection state
              // The prop selectedRegion is likely an API ID (e.g. 'toshkent')
              // We need to check if this region matches the selected API ID
              const isSelected = selectedRegion && (
                selectedRegion === region.id || 
                ID_MAP[region.id] === selectedRegion
              );
              
              const isHovered = hoveredRegion === region.id;

              return (
                <motion.path
                  key={region.id}
                  d={region.d}
                  id={region.id}
                  initial={{ opacity: 0, scale: 0.95 }}
                  animate={{ 
                    opacity: 1, 
                    scale: 1,
                    fill: isSelected ? 'rgba(59, 130, 246, 0.8)' : isHovered ? 'rgba(59, 130, 246, 0.4)' : 'rgba(255, 255, 255, 0.1)',
                    stroke: isSelected ? '#ffffff' : 'rgba(255, 255, 255, 0.4)',
                    strokeWidth: isSelected ? 2 : 0.5,
                    zIndex: isHovered || isSelected ? 10 : 1
                  }}
                  transition={{ duration: 0.3 }}
                  onMouseEnter={() => setHoveredRegion(region.id)}
                  onMouseLeave={() => setHoveredRegion(null)}
                  onClick={() => handleRegionClick(region.id)}
                  className="cursor-pointer transition-colors duration-200 outline-none"
                  whileHover={{ scale: 1.01 }}
                  whileTap={{ scale: 0.99 }}
                />
              );
            })}
          </g>
        </svg>

        <AnimatePresence>
          {hoveredRegion && (
            <motion.div
              initial={{ opacity: 0, y: 10, scale: 0.9 }}
              animate={{ opacity: 1, y: 0, scale: 1 }}
              exit={{ opacity: 0, y: 5, scale: 0.9 }}
              transition={{ duration: 0.15 }}
              className="absolute pointer-events-none bg-black/80 text-white px-3 py-1.5 rounded-lg text-sm font-medium backdrop-blur-md border border-white/10 z-50 shadow-lg"
              style={{
                left: '50%',
                bottom: '20px',
                transform: 'translateX(-50%)'
              }}
            >
              {regions.find(r => r.id === hoveredRegion)?.name}
            </motion.div>
          )}
        </AnimatePresence>
      </div>
    </div>
  );
}
